pragma solidity ^0.8.17;

import {Ownable} from "openzeppelin-contracts/contracts/access/Ownable.sol";
import {Address} from "openzeppelin-contracts/contracts/utils/Address.sol";

abstract contract Balances is Ownable {

    using Address for address payable;

    event OwnerCutSet(uint256 indexed ownerCut);
    event ReferrerCutSet(uint256 indexed referrerCut);
    event AddressWithdrew(address indexed _address, uint256 indexed amount);

    // A mapping to store the balance of each referrer.
    mapping (address => uint256) public balances;

    // A mapping to store the referrer cut percentage for each referrer.
    mapping (address => uint256) public referrerCuts;

    // A variable to store the total balance of all referrers.
    uint256 public totalBalance;

    // The cut taken by the owner of the contract.
    uint256 public ownerCut;

    constructor() {
        // Default cut is 2% (200/10000).
        ownerCut = 200;  
    }

    /**
     * @notice A function to allow referrers, name owners, or the contract owner to withdraw.
     */

    function withdraw() public {

        //get the address of the sender
        address payable sender = payable(msg.sender);

        // Withdraw the owner's balance if the sender is the contract owner.
        if(sender == owner()) { 

            // Calculate the amount that can be withdrawn.
            uint256 ownerAmount = address(this).balance - totalBalance;

            require(ownerAmount > 0, "Owner balance is 0");

            emit AddressWithdrew(sender, ownerAmount);

            // Send the amount to the contract owner's address.
            sender.sendValue(ownerAmount);

        } else { 

            // Require that the senders balance is greater than 0.
            require(balances[sender] > 0, "Address's balance is 0");

            // Calculate the amount that the sender can withdraw
            uint256 amount = balances[sender];

            // Set the sender balance to 0.
            balances[sender] = 0;

            // Decrease the total referrer balance.
            totalBalance -= amount;

            emit AddressWithdrew(sender, amount);

            // Send the amount to the address.
            sender.sendValue(amount);
        }
    }

    /**
    * @notice A function to set the cut for the owner of the contract.
    * @param _ownerCut The cut for the owner of the contract.
    */

    function setOwnerCut(uint256 _ownerCut) public {
        require(msg.sender == owner(), "Only the owner can set the cut");
        require(_ownerCut <= 500, "Owner cut cannot be more than 5%");
        ownerCut = _ownerCut;
        emit OwnerCutSet(_ownerCut);
    }

    /**
     * @notice A function to set the referrer cut percentage for a specific referrer.
     * @param _referrerCut The percentage cut given to the referrer (0-10%).
     */

    function setReferrerCut(uint _referrerCut) public {
        require(_referrerCut <= 1000, "Referrer cut cannot be more than 10%");
        referrerCuts[msg.sender] = _referrerCut;
        emit ReferrerCutSet(_referrerCut);
    } 
}","keccak256":"0xca44837231ac89c333241f532cf4836a1100277bbd037a9b684441275ed86abc","license":"MIT"},"contracts/layertwo/optimism/wrapper/BytesUtilsSub.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

library BytesUtilsSub {
    /*
     * @dev Returns the keccak-256 hash of a byte range.
     * @param self The byte string to hash.
     * @param offset The position to start hashing at.
     * @param len The number of bytes to hash.
     * @return The hash of the byte range.
     */
    function keccak(
        bytes memory self,
        uint256 offset,
        uint256 len
    ) internal pure returns (bytes32 ret) {
        require(offset + len <= self.length);
        assembly {
            ret := keccak256(add(add(self, 32), offset), len)
        }
    }

    /**
     * @dev Returns the ENS namehash of a DNS-encoded name.
     * @param self The DNS-encoded name to hash.
     * @param offset The offset at which to start hashing.
     * @return The namehash of the name.
     */
    function namehash(bytes memory self, uint256 offset)
        internal
        pure
        returns (bytes32)
    {
        (bytes32 labelhash, uint256 newOffset) = readLabel(self, offset);
        if (labelhash == bytes32(0)) {
            require(offset == self.length - 1, "namehash: Junk at end of name");
            return bytes32(0);
        }
        return
            keccak256(abi.encodePacked(namehash(self, newOffset), labelhash));
    }

    /**
     * @dev Returns the keccak-256 hash of a DNS-encoded label, and the offset to the start of the next label.
     * @param self The byte string to read a label from.
     * @param idx The index to read a label at.
     * @return labelhash The hash of the label at the specified index, or 0 if it is the last label.
     * @return newIdx The index of the start of the next label.
     */
    function readLabel(bytes memory self, uint256 idx)
        internal
        pure
        returns (bytes32 labelhash, uint256 newIdx)
    {
        require(idx < self.length, "readLabel: Index out of bounds");
        uint256 len = uint256(uint8(self[idx]));
        if (len > 0) {
            labelhash = keccak(self, idx + 1, len);
        } else {
            labelhash = bytes32(0);
        }
        newIdx = idx + len + 1;
    }

    /**
     * @dev This function takes a bytes input which represents the DNS name and
     * returns the first label of a domain name in DNS format.
     * @param domain The domain in DNS format wherein the length precedes each label
     * and is terminted with a 0x0 byte, e.g. "cb.id" => [0x02,0x63,0x62,0x02,0x69,0x64,0x00].
     * @return string memory the first label.
     */

    function getFirstLabel(bytes memory domain) internal pure returns (string memory, uint256) {

        // Get the first byte of the domain which represents the length of the first label
        uint256 labelLength = uint256(uint8(domain[0]));

        // Check to make sure the label length is less than the domain length and greater than zero. 
        require(labelLength < domain.length && labelLength > 0);

        // Create a new byte array to hold the first label
        bytes memory firstLabel = new bytes(labelLength);

        // Iterate through the domain bytes to copy the first label to the new byte array
        // skipping the first byte which represents the length of the first label.
        for (uint256 i = 0; i < labelLength; ++i) {
            firstLabel[i] = domain[i+1];
        }

        // Convert the first label to string and return, with the length of the first label.
        return (string(firstLabel), labelLength);
    }

    /**
     * @dev This function takes a bytes input which represents the DNS name and
     * returns the TLD (last label) of a domain name. A domain can have a maximum of 10 labels.
     * @param domain bytes memory.
     * @return string memory the TLD.
     */

    function getTLD(bytes memory domain) internal pure returns (string memory) {
        // Variable used to keep track of the level count.

        uint levels = 0;
        // Variable used to keep track of the index of each length byte.

        // Iterate through the domain bytes. 
        for (uint i = 0; i < domain.length; i++) {

            // If level count exceed 10, break the loop.
            if (levels > 10) {
                break;
            }

            // Get the label length from the current byte.
            uint labelLength = uint(uint8(domain[i]));

            // Check to make sure we have found the terminal byte and it is equal to zero.
            if(i + labelLength + 2 == domain.length && domain[labelLength + i + 1] == 0) {

                // Create a new byte array to hold the TLD.
                bytes memory lastLabel = new bytes(labelLength);

                // Copy the TLD from the domain array to the new byte array.
                for (uint j = 0; j < labelLength; j++) {
                    lastLabel[j] = domain[i + 1 + j];
                }

                // Convert the TLD to string and return.
                return string(lastLabel);
            }

            // Move to the next label
            i += labelLength;

            // Increment the level count.
            levels++;
        }

        // Revert if TLD not found.
        revert("TLD not found");
    }
}","keccak256":"0xbd70dd8fe5f4c96b82049267f5a332c6c97bff8c2c8c014ceaa7f52f80a16aa2","license":"MIT"},"contracts/layertwo/optimism/wrapper/L2RenewalControllerBase.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {INameWrapper} from "ens-contracts/wrapper/INameWrapper.sol";
import {INameWrapperUpgrade} from "ens-contracts/wrapper/INameWrapperUpgrade.sol";
import {StringUtils} from "ens-contracts/ethregistrar/StringUtils.sol";
import {Balances} from "optimism/wrapper/Balances.sol";
import {BytesUtilsSub} from "./BytesUtilsSub.sol";
import {ERC165} from "openzeppelin-contracts/contracts/utils/introspection/ERC165.sol";
import {IRenewalController} from "optimism/wrapper/interfaces/IRenewalController.sol";
import {Ownable} from "openzeppelin-contracts/contracts/access/Ownable.sol";
import {ERC20Recoverable} from "ens-contracts/utils/ERC20Recoverable.sol";
import {Address} from "openzeppelin-contracts/contracts/utils/Address.sol";

error InsufficientValue();
error UnauthorizedAddress(bytes32 node);

abstract contract L2RenewalControllerBase is 
    IRenewalController,
    ERC165,
    Ownable,
    Balances,
    ERC20Recoverable
    {

    using Address for address payable;

    event NameWrapperVersionUpdated();
    event SubnameWrapperVersionUpdated();

    using StringUtils for *;
    using BytesUtilsSub for bytes;

    // The NameWrapper and SubnameWrapper can be upgraded. 
    address[] public nameWrappers = new address[](1);

    constructor(
        INameWrapper _nameWrapper
    ) {

        nameWrappers[0] = address(_nameWrapper);

    }

    /**
     * @notice If the NameWrapper smart contract is upgraded, add the new version.
     * @param _nameWrapper The next version of the NameWrapper
     */

    function addNextNameWrapperVersion(
        INameWrapperUpgrade _nameWrapper
    ) public onlyOwner {

        nameWrappers.push(address(_nameWrapper));

        emit NameWrapperVersionUpdated();
    }

    /**
    * @dev Function to renew a name for a specified duration. 
    * @param name The name to be renewed in DNS format.
    * @param duration The duration for which the name should be renewed in years.
    */

    function renew(bytes calldata name, address referrer, uint256 duration)
        external
        payable
    {        

        // Renew with the latest version of the NameWrapper and SubnameWrapper contracts.
        renewWithVersions(
            nameWrappers.length-1,    
            name,
            referrer,
            duration);
    }

    /**
    * @notice Function to renew a name for a specified duration. 
    * @dev This function is allows for the upgradeing of the NameWrapper and SubnameWrapper contracts.
    * It is not possible to know what the interface of the upgarded contracts will be, so we assume that
    * they will be compatible with the current version of the contracts.
    * @param nameWrapperV The version of the NameWrapper.
    * @param name The name to be renewed in DNS format.
    * @param duration The duration for which the name should be renewed in years.
    */

    function renewWithVersions(
        uint256 nameWrapperV,
        bytes calldata name, 
        address referrer, 
        uint256 duration
        )
        public
        payable
    {        

        bytes32 parentNode;
        bytes32 node;
        bytes32 labelhash;

        // Create a block to solve a stack too deep error.
        { 
            uint256 offset;
            (labelhash, offset) = name.readLabel(0);
            parentNode = name.namehash(offset);
            node = _makeNode(parentNode, labelhash);
        }

        // Get the owners of the name and the parent name.
        address parentOwner = INameWrapper(nameWrappers[nameWrapperV]).ownerOf(uint256(parentNode));
        address nodeOwner = INameWrapper(nameWrappers[nameWrapperV]).ownerOf(uint256(node));

        // Check to make sure the caller (msg.sender) is authorised to renew the name.
        if( msg.sender != nodeOwner && !INameWrapper(nameWrappers[nameWrapperV]).isApprovedForAll(nodeOwner, msg.sender)){
            revert UnauthorizedAddress(node);
        }

        uint64 expiry;

        // Create a block to solve a stack too deep error.
        {
            // Get the previous expiry. 
            (,, uint64 nodeExpiry) = INameWrapper(nameWrappers[nameWrapperV]).getData(uint256(node));

            // Check to see if the duration is too long and
            // if it is set the duration.
            (,, uint64 parentExpiry) = INameWrapper(nameWrappers[nameWrapperV]).getData(uint256(parentNode));
            if (nodeExpiry + duration > parentExpiry) {
                duration = parentExpiry - nodeExpiry;
            }

            // Set the expiry
            expiry =  uint64(nodeExpiry + duration);
        }

        // Get the price for the duration.
        (uint256 priceEth,) = rentPrice(name, duration);
        if (msg.value < priceEth) {
            revert InsufficientValue();
        }

        // Create a block to solve a stack too deep error.
        {
            uint256 referrerAmount;

            // If a referrer is specified then calculate the amount to be given to the referrer.
            if (referrer != address(0)) {

                // Calculate the amount to be given to the referrer.
                referrerAmount = priceEth * referrerCuts[referrer] / 10000;

                // Increase the referrer's balance
                balances[referrer] += referrerAmount;
            }

            // Calculate the amount to be given to the owner of the contract. 
            // We don't need a balance for the owner of the contract because the owner
            // can withdraw any funds in the contract minus total balances. 
            uint256 ownerAmount = priceEth * ownerCut / 10000;


            // Increase the owner of the parent name's balance minus the
            // referrer amount and the owner amount.
            balances[parentOwner] += priceEth - referrerAmount - ownerAmount;

            // Increase the total balances
            totalBalance += priceEth - ownerAmount;
        }

        INameWrapper(nameWrappers[nameWrapperV]).extendExpiry(
            parentNode,
            labelhash,
            expiry
        );

        emit NameRenewed(name, priceEth, expiry);

        // If the caller paid too much refund the amount overpaid. 
        if (msg.value > priceEth) {
            payable(msg.sender).sendValue(msg.value - priceEth);
        }

    }

    /**
    * @notice Returns the price to rent a subdomain for a given duration.
    * @param name The name of the subdomain.
    * @param duration The duration of the rental. 
    */ 

    function rentPrice(bytes calldata name, uint256 duration)
        public
        view
        virtual
        returns (uint256, uint256);

    /**
    * @param interfaceId The interface identifier, as specified in ERC-165.
    * @return `true` if the contract implements `interfaceID`
    */ 

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override
        returns (bool)
    {
        return
            interfaceId == type(IRenewalController).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    /**
     * @dev Returns the node for a namehash and labelhash.
     * @param parentNode The namehash of the parent node.
     * @param labelhash The labelhash of the label.
     * @return The node for the namehash and labelhash.
     */
    function _makeNode(bytes32 parentNode, bytes32 labelhash)
        private
        pure
        returns (bytes32)
    {
        return keccak256(abi.encodePacked(parentNode, labelhash));
    }
}","keccak256":"0xba0907da1a8eb596b5aef1c0ebc8019043c87043eb2d424a657a12eff106a59f","license":"MIT"},"contracts/layertwo/optimism/wrapper/interfaces/IAggregatorInterface.sol":{"content":"//SPDX-License-Identifier: MIT
