// Chainlink oracle interface
interface IAggregatorInterface {
    function latestAnswer() external view returns (int256);
}","keccak256":"0x39cfb36ad94daab8e3814fad5740f6ac747d11cba399812267333bc1e32ab28f","license":"MIT"},"contracts/layertwo/optimism/wrapper/interfaces/IRenewalController.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

interface IRenewalController {

    event NameRenewed(
        bytes indexed name,
        uint256 indexed price,
        uint64 indexed expiry
    );

    function renew(bytes calldata name, address referrer, uint256 duration)
        external
        payable;

    function rentPrice(bytes calldata name, uint256 duration)
        external
        view
        returns (uint256 weiPrice, uint256 usdPrice);
